mapper.insert:
INSERT INTO work_logs (
  wl_work_datetime, wl_car_model, wl_product_color, wl_product_code, wl_product_name, wl_quantity
) VALUES (
           #{workDatetime}, #{carModel}, #{productColor}, #{productCode}, #{productName}, #{quantity}
         )

mapper.update:
UPDATE work_logs
SET wl_completed_at = #{completedAt}
WHERE wl_id = #{id}

mapper.select:
SELECT 
FROM work_logs
WHERE wl_work_datetime >= #{startDate, jdbcType=VARCHAR}
AND wl_work_datetime <= #{endDate, jdbcType=VARCHAR}
ORDER BY wl_work_datetime ASC
